import './App.css';
import React, {useState} from "react"
export function App() {
  const [count,setCount]=useState(0);
  const updateCount =()=>{setCount(count+1)}
  const dismisCount=()=>{setCount(count-1)}
  const rezultZero=()=>{setCount(count)}
  return (
    <div>
      <span className="counter">{count}</span>
      <button className="counter-button" onClick={updateCount}>
        +1
      </button>
      <button className="dismisCounter-button" onClick={dismisCount}>
        -1
      </button>
      <button className="zero-button" onClick={rezultZero}>
        0
      </button>
    </div>
  );
}

export default App;
